mapper.select:
SELECT SUM(CASE WHEN TUI.AGE < 10 THEN 1 ELSE 0 END) AS A0, SUM(CASE WHEN TUI.AGE < 20 AND TUI.AGE >= 10 THEN 1 ELSE 0 END) AS A1, SUM(CASE WHEN TUI.AGE < 30 AND TUI.AGE >= 20 THEN 1 ELSE 0 END) AS A2, SUM(CASE WHEN TUI.AGE < 40 AND TUI.AGE >= 30 THEN 1 ELSE 0 END) AS A3, SUM(CASE WHEN TUI.AGE < 50 AND TUI.AGE >= 40 THEN 1 ELSE 0 END) AS A4, SUM(CASE WHEN TUI.AGE >= 50 THEN 1 ELSE 0 END) AS A5 FROM TB_USER_INFO TUI, (SELECT T.USER_ID FROM TB_VISITOR T
YYYY = #{yyyy}
AND MM = #{mm}
AND DD = #{dd}
) TV WHERE TUI.USER_ID = TV.USER_ID

SELECT (SELECT TUI.GENDER
		  FROM TB_USER_INFO TUI
		 WHERE TUI.USER_ID = TV.USER_ID) AS GENDER,
	   COUNT(*) AS CNT
  FROM TB_VISITOR TV



		YYYY = #{yyyy} 


		AND MM = #{mm} 


		AND DD = #{dd} 



 GROUP BY GENDER

SELECT TUI.GENDER,
	   COUNT(*) AS CNT
  FROM TB_USER_INFO TUI,
  	   (SELECT DISTINCT T.USER_ID
  	      FROM TB_VISITOR T



		YYYY = #{yyyy} 


		AND MM = #{mm} 


		AND DD = #{dd} 



  	   ) TV
 WHERE TUI.USER_ID = TV.USER_ID
 GROUP BY GENDER
